package javabasic;

public class MultyandDivision {
	//global variable
	int a=10;
	int b= 5;
	public void multy() {
		
		System.out.println(a*b);
	}
	public void divi() {
		System.out.println(a/b);
	}
	
	
	
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		MultyandDivision md =new MultyandDivision();
		//md.a=10;
		//md.b=15;
		md.multy();
		md.divi();
	}

}
